let buttons=document.querySelectorAll(".cv"),category=new Map([["suv",document.querySelectorAll(".cat-suv")],["business",document.querySelectorAll(".cat-business")],["sport",document.querySelectorAll(".cat-sport")],["premium",document.querySelectorAll(".cat-premium")],["comfort",document.querySelectorAll(".cat-comfort")]]),allCards=document.querySelectorAll(".block__grid");function DisplayCards(e){allCards.forEach((e=>{e.style.display="none"})),category.get(e).forEach((e=>{e.style.display="flex"}))}for(let e of buttons)e.addEventListener("click",(function(t){t.preventDefault(),e.classList.contains("cv_active")||(buttons.forEach((e=>{e.classList.remove("cv_active"),this.classList.add("cv_active")})),DisplayCards(this.dataset.category))}));document.querySelector(".cv_all").addEventListener("click",(function(e){e.preventDefault(),allCards.forEach((e=>{e.style.display="flex"})),buttons.forEach((e=>{e.classList.remove("cv_active")}))}));