var slider=document.querySelector(".images1"),prev=document.querySelector(".slider-prev"),next=document.querySelector(".slider-next"),slideWidth=document.querySelector(".slider-img").offsetWidth+20,slideNum=0,slidesAll=document.querySelectorAll(".slider-img").length;next.addEventListener("click",(function(e){e.preventDefault(),slideNum<slidesAll-2?slideNum++:slideNum=0,slider.style.transform=`translateX(-${slideNum*slideWidth}px)`})),prev.addEventListener("click",(function(e){e.preventDefault(),slideNum>0?slideNum--:slideNum=slidesAll-2,slider.style.transform=`translateX(-${slideNum*slideWidth}px)`}));